mapper.select:
SELECT IFNULL(SUM(RP.point),0)
FROM reactionPoint AS
RP
WHERE RP.relTypeCode = #{relTypeCode}
AND RP.relId = #{relId}
AND
RP.memberId = #{memberId};

mapper.update:
UPDATE reactionPoint AS RP
INNER JOIN (
SELECT *
FROM article AS A
) AS A
ON A.id = RP.relId
SET RP.`point` = #{usersReaction}*-1
WHERE RP.relId = #{relId}
AND RP.relTypeCode =
#{relTypeCode}
AND RP.memberId = #{loginedMemberId}

mapper.delete:
DELETE FROM reactionPoint
WHERE memberId = #{memberId}
AND relTypeCode = #{relTypeCode}
AND relId = #{relId}
